<?php /** * @copyright        2016 opencart.cn - All Rights Reserved * @link             http://www.guangdawangluo.com * @author           TL <mengwb@opencart.cn> * @created          2016-12-12 16:04:00 * @modified         2016-12-12 16:39:36 */class ModelExtensionPaymentQrcodeweipay extends Model{      public function getMethod($address, $total)      {        $this->load->language('extension/payment/qrcodeweipay');        if ($this->config->get('qrcodeweipay_total') > 0 && $this->config->get('qrcodeweipay_total') > $total) {            $status = false;        } else {            $status = true;        }        $method_data = array();        if ($status) {              $method_data = array(                 'code'         => 'qrcodeweipay',                'title'      => $this->language->get('text_title'),                'terms'      => '',                'sort_order' => $this->config->get('qrcodeweipay_sort_order')            );        }        return $method_data;    }}